import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  // title = 'Registration Form';

  getValue(username: string, password: string){
    var data = {'username':username,'password':password};
    console.log(data)
    localStorage.setItem("users", JSON.stringify(data))
  }
}
<!DOCTYPE html>
<html>
<head>
  <title>Login Page</title>
</head>
<body>
  <div class="container">
    <h1>Login</h1>
    <form>
      <label for="username">Username:</label>
      <input type="text" #username placeholder="Username" name="username" required>
      <label for="password">Password:</label>
      <input type="text" #password placeholder="Password" name="password" required>
      <button (click) = "getValue(username.value, password.value)" type="submit">
        <a href="../profile">Login</a>
        <!-- Login -->
      </button>
    </form>
  </div>
</body>
</html>
